import React from 'react'

export default function TodoItem({title, desc}) {
    let localStyling = {
        border: "1px solid black",
        borderRadius : "10px"
    }
  return (
    <div className="p-3 m-2" style={localStyling}>
        <h5>{title}</h5>
        <p>
            {desc}
        </p>
        <button className="btn btn-danger btn-sm"> <i class="bi bi-trash"></i> Delete</button>
        <button className="btn btn-success btn-sm mx-1"> <i class="bi bi-check-lg"></i> Mark Complete</button>
    </div>
  )
}
